import re

def search_passwords(file_path):
    try:
        with open(file_path, 'r') as file:
            content = file.read()
            android_passwords = re.findall(r'Android Password: (.*)', content)
            ios_passwords = re.findall(r'iOS Password: (.*)', content)
            return android_passwords, ios_passwords
    except FileNotFoundError:
        print(f"File not found: {file_path}")
        return [], []

def main():
    file_path = 'path_to_your_file.txt'  # replace with your file path
    android_passwords, ios_passwords = search_passwords(file_path)

